// a menu component that displays login/logout buttons depending on the user's auth status
// and a link to the user's profile page
import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";

export const Menu = ({ auth, logoutUser }) => {
  const { isAuthenticated, user } = auth;

  const onLogoutClick = (e) => {
    e.preventDefault();
    logoutUser();
  };

  const authLinks = (
    <div className="navbar-nav ml-auto">
      <li className="nav-item">
        <Link className="nav-link" to="/profile">
          <img
            className="rounded-circle"
            src={user.avatar}
            alt={user.name}
            style={{ width: "25px", marginRight: "5px" }}
            title="You must have a Gravatar connected to your email to display an image"
          />
          Profile
        </Link>
      </li>
      <li className="nav-item">
        <a href="/" className="nav-link" onClick={onLogoutClick}>
          Logout
        </a>
      </li>
    </div>
  );

  const guestLinks = (
    <div className="navbar-nav ml-auto">
      <li className="nav-item">
        <Link className="nav-link" to="/register">
          Sign Up
        </Link>
      </li>
      <li className="nav-item">
        <Link className="nav-link" to="/login">
          Login
        </Link>
      </li>
    </div>
  );

  return (
    <nav className="navbar navbar-expand-sm navbar-dark bg-dark mb-4">
      <div className="container">
        <Link className="navbar-brand" to="/">
          DevConnector
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#mobile-nav"
        >
          <span className="navbar-toggler-icon" />
        </button>

        <div className="collapse navbar-collapse" id="mobile-nav">
          {isAuthenticated ? authLinks : guestLinks}
        </div>
      </div>
    </nav>
  );
};
